参考代码 <pre><code>struct TreeNode {
    int key;
    TreeNode *left;
    TreeNode *right;
    // 维护其他信息，如高度，节点数量等
    int size;  // 当前节点为根的子树大小
    int count; // 当前节点的重复数量

    TreeNode(int value)
        : key(value), size(1), count(1), left(nullptr), right(nullptr) {}
};

TreeNode *findMinNode(TreeNode *root) {
    while (root-&gt;left != nullptr) {
        root = root-&gt;left;
    }
    return root;
}

bool search(TreeNode *root, int target) {
    if (root == nullptr) {
        return false;
    }
    if (root-&gt;key == target) {
        return true;
    } else if (target &lt; root-&gt;key) {
        return search(root-&gt;left, target);
    } else {
        return search(root-&gt;right, target);
    }
}

TreeNode *insert(TreeNode *root, int value) {
    if (root == nullptr) {
        return new TreeNode(value);
    }
    if (value &lt; root-&gt;key) {
        root-&gt;left = insert(root-&gt;left, value);
    } else if (value &gt; root-&gt;key) {
        root-&gt;right = insert(root-&gt;right, value);
    } else {
        root-&gt;count++; // 节点值相等，增加重复数量
    }
    root-&gt;size = root-&gt;count + (root-&gt;left ? root-&gt;left-&gt;size : 0) +
                 (root-&gt;right ? root-&gt;right-&gt;size : 0); // 更新节点的子树大小
    return root;
}

// 此处返回值为删除 value 后的新 root
TreeNode *remove(TreeNode *root, int value) {
    if (root == nullptr) {
        return root;
    }
    if (value &lt; root-&gt;key) {
        root-&gt;left = remove(root-&gt;left, value);
    } else if (value &gt; root-&gt;key) {
        root-&gt;right = remove(root-&gt;right, value);
    } else {
        if (root-&gt;count &gt; 1) {
            root-&gt;count--; // 节点重复数量大于1，减少重复数量
        } else {
            if (root-&gt;left == nullptr) {
                TreeNode *temp = root-&gt;right;
                delete root;
                return temp;
            } else if (root-&gt;right == nullptr) {
                TreeNode *temp = root-&gt;left;
                delete root;
                return temp;
            } else {
                TreeNode *successor = findMinNode(root-&gt;right);
                root-&gt;key = successor-&gt;key;
                root-&gt;count = successor-&gt;count; // 更新重复数量
                // 当 successor-&gt;count &gt; 1时，也应该删除该节点，否则
                // 后续的删除只会减少重复数量
                successor-&gt;count = 1;
                root-&gt;right = remove(root-&gt;right, successor-&gt;key);
            }
        }
    }
    // 继续维护size，不写成 --root-&gt;size;
    // 是因为value可能不在树中，从而可能未发生删除
    root-&gt;size = root-&gt;count + (root-&gt;left ? root-&gt;left-&gt;size : 0) +
                 (root-&gt;right ? root-&gt;right-&gt;size : 0);
    return root;
}
</code></pre>

参考代码 <pre><code>#include &lt;bits/stdc++.h&gt;
using namespace std;

struct AVL_tree {
    struct point {
        int val;
        int height;
        point *ls;
        point *rs;
        point *fa;
        point() {
            val = 0;
            height = 1;
            ls = rs = fa = NULL;
        }
    };
    point *root;
    AVL_tree() { root = NULL; }
    inline void push_up(point *p) {
        if (p != NULL) p-&gt;height = max(h(p-&gt;ls), h(p-&gt;rs)) + 1;
    }
    inline int h(point *p) {
        if (p == NULL) return 0;
        return p-&gt;height;
    }
    inline void take_place(point *q, point *p) {
        if (root == p) root = q;
        else if (p-&gt;fa-&gt;ls == p) p-&gt;fa-&gt;ls = q;
        else p-&gt;fa-&gt;rs = q;
        if (q != NULL) q-&gt;fa = p-&gt;fa;
    }
    point *search(point *p, int val) {
        if (p == NULL || val == p-&gt;val) return p;
        if (val &lt; p-&gt;val) return search(p-&gt;ls, val);
        return search(p-&gt;rs, val);
    }
    point *maximum(point *p) {
        while (p-&gt;rs != NULL) p = p-&gt;rs;
        return p;
    }
    point *minimum(point *p) {
        while (p-&gt;ls != NULL) p = p-&gt;ls;
        return p;
    }
    void turn_right(point *p) {
        take_place(p-&gt;ls, p);
        p-&gt;fa = p-&gt;ls;
        p-&gt;ls = p-&gt;fa-&gt;rs;
        if (p-&gt;fa-&gt;rs != NULL) p-&gt;fa-&gt;rs-&gt;fa = p;
        p-&gt;fa-&gt;rs = p;
    }
    void turn_left(point *p) {
        take_place(p-&gt;rs, p);
        p-&gt;fa = p-&gt;rs;
        p-&gt;rs = p-&gt;fa-&gt;ls;
        if (p-&gt;fa-&gt;ls != NULL) p-&gt;fa-&gt;ls-&gt;fa = p;
        p-&gt;fa-&gt;ls = p;
    }
    void balance(point *p) {
        if (h(p-&gt;ls) - h(p-&gt;rs) == 2) {
            if (h(p-&gt;ls-&gt;rs) &gt; h(p-&gt;ls-&gt;ls)) turn_left(p-&gt;ls);
            turn_right(p);
        } else if (h(p-&gt;ls) - h(p-&gt;rs) == -2) {
            if (h(p-&gt;rs-&gt;ls) &gt; h(p-&gt;rs-&gt;rs)) turn_right(p-&gt;rs);
            turn_left(p);
        }
    }
    void insert(point *r, point *p) {
        if (p-&gt;val &lt; r-&gt;val) {
            if (r-&gt;ls == NULL) {
                r-&gt;ls = p;
                p-&gt;fa = r;
            } else insert(r-&gt;ls, p);
        } else {
            if (r-&gt;rs == NULL) {
                r-&gt;rs = p;
                p-&gt;fa = r;
            } else insert(r-&gt;rs, p);
        }
        push_up(r);
        balance(r);
    }
    void ins(int val) {
        point *p = new point();
        p-&gt;val = val;
        if (root == NULL) {
            root = p;
            p-&gt;fa = NULL;
            return;
        }
        insert(root, p);
    }
    void print(point *p) {
        if (p != NULL) {
            print(p-&gt;ls);
            printf("%d ", p-&gt;val);
            print(p-&gt;rs);
        }
    }
    void del_one(point *p) {
        point *q = p-&gt;ls;
        if (q == NULL) q = p-&gt;rs;
        take_place(q, p);
        delete p;
    }
    void del(point *p) {
        point *q;
        if (p-&gt;ls == NULL || p-&gt;rs == NULL) q = p;
        else {
            q = minimum(p-&gt;rs);
            p-&gt;val = q-&gt;val;
        }
        point *r = q-&gt;fa;
        del_one(q);
        while (r != NULL) {
            push_up(r);
            balance(r);
            r = r-&gt;fa;
        }
    }
    void del(int val) {
        point *p = search(root, val);
        if (p == NULL) return;
        del(p);
    }
};
</code></pre>

参考代码 <pre><code>// 使用二叉堆优化的 Prim 算法。
#include &lt;cstring&gt;
#include &lt;iostream&gt;
#include &lt;queue&gt;
using namespace std;
constexpr int N = 5050, M = 2e5 + 10;

struct E {
    int v, w, x;
} e[M * 2];

int n, m, h[N], cnte;

void adde(int u, int v, int w) { e[++cnte] = E{v, w, h[u]}, h[u] = cnte; }

struct S {
    int u, d;
};

bool operator&lt;(const S &amp;x, const S &amp;y) { return x.d &gt; y.d; }

priority_queue&lt;S&gt; q;
int dis[N];
bool vis[N];

int res = 0, cnt = 0;

void Prim() {
    memset(dis, 0x3f, sizeof(dis));
    dis[1] = 0;
    q.push({1, 0});
    while (!q.empty()) {
        if (cnt &gt;= n) break;
        int u = q.top().u, d = q.top().d;
        q.pop();
        if (vis[u]) continue;
        vis[u] = true;
        ++cnt;
        res += d;
        for (int i = h[u]; i; i = e[i].x) {
            int v = e[i].v, w = e[i].w;
            if (w &lt; dis[v]) {
                dis[v] = w, q.push({v, w});
            }
        }
    }
}

int main() {
    cin &gt;&gt; n &gt;&gt; m;
    for (int i = 1, u, v, w; i &lt;= m; ++i) {
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; w, adde(u, v, w), adde(v, u, w);
    }
    Prim();
    if (cnt == n) cout &lt;&lt; res;
    else cout &lt;&lt; "No MST.";
    return 0;
}
</code></pre>

参考代码 <pre><code>#include &lt;algorithm&gt;
#include &lt;iostream&gt;
using namespace std;

int fa[1010]; // 定义父亲
int n, m, k;

struct edge {
    int u, v, w;
};

int l;
edge g[10010];

void add(int u, int v, int w) {
    l++;
    g[l].u = u;
    g[l].v = v;
    g[l].w = w;
}

// 标准并查集
int findroot(int x) { return fa[x] == x ? x : fa[x] = findroot(fa[x]); }

void Merge(int x, int y) {
    x = findroot(x);
    y = findroot(y);
    fa[x] = y;
}

bool cmp(edge A, edge B) { return A.w &lt; B.w; }

// Kruskal 算法
void kruskal() {
    int tot = 0; // 存已选了的边数
    int ans = 0; // 存总的代价
    for (int i = 1; i &lt;= m; i++) {
        int xr = findroot(g[i].u), yr = findroot(g[i].v);
        if (xr != yr) {    // 如果父亲不一样
            Merge(xr, yr); // 合并
            tot++;         // 边数增加
            ans += g[i].w; // 代价增加
        }
        if (tot &gt;= (n - k)) { // 检查选的边数是否满足 k 个棉花糖
            cout &lt;&lt; ans &lt;&lt; '\n';
            return;
        }
    }
    cout &lt;&lt; "No Answer\n"; // 无法连成
}

int main() {
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; k;
    for (int i = 1; i &lt;= n; i++) { // 初始化
        fa[i] = i;
    }
    for (int i = 1; i &lt;= m; i++) {
        int u, v, w;
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; w;
        add(u, v, w); // 添加边
    }
    sort(g + 1, g + m + 1, cmp); // 先按边权排序
    kruskal();
    return 0;
}
</code></pre>
